// SPDX-License-Identifier: BSL 1.1
pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@balancer-labs/v2-interfaces/contracts/pool-stable/StablePoolUserData.sol";

// if import "@balancer-labs/v2-interfaces/contracts/vault/IVault.sol" then we have a problem of
// re-declaration of IERC20, because balancer implement their own IERC20 (don't know why)
import "../../vendor/balancer/IVault.sol";
import "../../access-control/SuAuthenticated.sol";
import "../../interfaces/adapters/ILPAdapter.sol";
import "../../interfaces/adapters/IBasePoolExtended.sol";
import "../../interfaces/adapters/deprecated/IBalancerPriceOracle.sol";

/**
 * @dev
 * Default - It's pool that is not Composable, just StablePool, like AURA_BAL_LP
 * Composable - It's Balancer Composable Stable LP token, like WST_R_SFRX_LP
 * MetaStable - It's MetaStable LP token, like B-rETH-STABLE
*/
enum StablePoolType { Default, Composable, MetaStable }

struct LPInfoAdapter {
    StablePoolType poolType;
    bytes32 poolId;
    IVault vault;
    IERC20[] underlyingTokens;
    IERC20[] underlyingTokensExpanded;
}

interface IPoolWithActualSupply {
    function getActualSupply() external view returns (uint256);
}

/**
 * @title BalancerAdapterStable
 * @notice Adapter for Balancer Stable LP token.
 * @dev See ILPAdapter interface for full details.
 */
contract BalancerAdapterStable is SuAuthenticated, ILPAdapter {
    mapping(address => LPInfoAdapter) public lps; // Mapping from LP token to pool
    ISuOracle public ORACLE;
    using StablePoolUserData for bytes;

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
    }

    /**
      * @notice Register the given LP token address and set the LP info. We go to the depth = 2, that means, that if
      * LP token {lp} contains {lp1} and {erc20Token1} as underlying tokens,
      * where lp1 contains {erc20Token0} and {erc20Token2},
      * then we will register that {lp} has {erc20Token0}, {erc20Token2} and {erc20Token1} as underlying tokens.
      * @param lp LP token to find and set the corresponding info.
     **/
    function registerLP(address lp, StablePoolType poolType) external onlyAdmin {
        IVault vault = IBasePoolExtended(lp).getVault();
        if (address(vault) == address(0)) revert IsNotLP(lp);
        if (address(lps[lp].vault) != address(0)) revert AlreadyRegistered(lp);

        bytes32 poolId = IBasePoolExtended(lp).getPoolId();

        lps[lp].poolId = poolId;
        lps[lp].vault = vault;
        lps[lp].poolType = poolType;

        (lps[lp].underlyingTokens,,) = vault.getPoolTokens(poolId);

        uint256 nTokens = lps[lp].underlyingTokens.length;
        for (uint16 i = 0; i < nTokens; ++i) {
            IERC20 currentToken = lps[lp].underlyingTokens[i];
            if (isAdapterLP(address(currentToken))) {
                IERC20[] memory newUnderlyingTokens;
                LPInfoAdapter memory underlyingLPTokenInfo = lps[address(currentToken)];
                (newUnderlyingTokens,,) = underlyingLPTokenInfo.vault.getPoolTokens(
                    underlyingLPTokenInfo.poolId
                );
                for (uint16 j = 0; j < newUnderlyingTokens.length; ++j) {
                    lps[lp].underlyingTokensExpanded.push(newUnderlyingTokens[j]);
                }
            } else {
                lps[lp].underlyingTokensExpanded.push(currentToken);
            }
        }
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return address(lps[asset].vault) != address(0);
    }

    function _getFiatPriceTimeWeightedAverage(address asset) internal view returns (uint256) {
        LPInfoAdapter memory lpInfo = lps[asset];
        IBalancerPriceOracle.OracleAverageQuery[] memory query = new IBalancerPriceOracle.OracleAverageQuery[](1);
        query[0] = IBalancerPriceOracle.OracleAverageQuery(
            IBalancerPriceOracle.Variable.BPT_PRICE,
            120, // the average over the past 2 minutes (and 10 seconds because of `ago` argument)
            10 // last price that included in this average was 10 seconds ago
        );

        // TODO: why price for RETH_METASTABLE_LP is not correct?
        // Why we should multiply getTimeWeightedAverage not to firstTokenPrice, but on second?
        uint256 firstTokenPrice = ORACLE.getFiatPrice1e18(address(lpInfo.underlyingTokens[0]));
        uint256 price = IBalancerPriceOracle(asset).getTimeWeightedAverage(query)[0];
        return firstTokenPrice * price / 1e18;
    }

    function getFiatPrice1e18(address asset) external view returns (uint256) {
        revert LPOracleNotReady();
//        if (!isAdapterLP(asset)) revert IsNotLP(asset);
//        LPInfoAdapter memory lpInfo = lps[asset];
//
//        if (lpInfo.poolType == StablePoolType.MetaStable) {
//            return _getFiatPriceTimeWeightedAverage(asset);
//        }
//
//        // TODO: check - getRate is more gas-efficient, but it isn't secure + we need to store base underline token
//        // can be manipulated: https://forum.balancer.fi/t/reentrancy-vulnerability-scope-expanded/4345
//        return getFiatPrice1e18Unsafe(asset);
    }

    function getFiatPrice1e18Unsafe(address asset) public view returns (uint256) {
        LPInfoAdapter memory lpInfo = lps[asset];

        uint256 sumBalances = 0;
        uint256 nTokens = lpInfo.underlyingTokens.length;
        (,uint256[] memory newUnderlyingTokenBalances,) = lpInfo.vault.getPoolTokens(lpInfo.poolId);
        for (uint256 i = 0; i < nTokens; ++i) {
            address underlyingTokenAddress = address(lpInfo.underlyingTokens[i]);
            if (underlyingTokenAddress != asset) {
                sumBalances += newUnderlyingTokenBalances[i] * ORACLE.getFiatPrice1e18(underlyingTokenAddress);
            }
        }

        uint256 totalSupply = lpInfo.poolType == StablePoolType.Composable
            ? IPoolWithActualSupply(asset).getActualSupply()
            : IERC20(asset).totalSupply();

        return sumBalances / totalSupply;
    }

    // For some LPs vault returns the first address in underlyingTokens as the LP token address =>
    // after all _withdrawInternal calls we can receive array with length < underlyingTokensExpanded.length
    function _withdrawInternal(address asset, uint256 amount) internal returns (WithdrawResult[] memory results) {
        LPInfoAdapter memory lpInfo = lps[asset];
        uint256 nTokens = lpInfo.underlyingTokens.length;
        address vaultAddress = address(lpInfo.vault);
        if (asset != vaultAddress && IERC20(asset).allowance(address(this), vaultAddress) < amount) {
            IERC20(asset).approve(vaultAddress, amount);
        }

        uint256[] memory balancesBefore = new uint256[](nTokens);
        for (uint16 i = 0; i < nTokens; ++i) {
            balancesBefore[i] = lpInfo.underlyingTokens[i].balanceOf(address(this));
        }

        IAsset[] memory assets = new IAsset[](nTokens);
        uint256[] memory minAmountsOut = new uint[](nTokens);
        for (uint16 i = 0; i < nTokens; ++i) {
            assets[i] = IAsset(address(lpInfo.underlyingTokens[i]));
            minAmountsOut[i] = 0;
        }
        lpInfo.vault.exitPool(
            lpInfo.poolId,
            payable(address(this)),
            payable(address(this)),
            IVault.ExitPoolRequest({
                assets: assets,
                minAmountsOut: minAmountsOut,
                userData: abi.encode(StablePoolUserData.ExitKind.EXACT_BPT_IN_FOR_ALL_TOKENS_OUT, amount),
                toInternalBalance: false
            })
        );

        // For some LPs vault returns the first address in underlyingTokens as the LP token address
        if (address(lpInfo.underlyingTokens[0]) == asset) {
            results = new WithdrawResult[](nTokens - 1);
            for (uint16 i = 1; i < nTokens; ++i) {
                uint256 underlineTokenAmount = lpInfo.underlyingTokens[i].balanceOf(address(this)) - balancesBefore[i];
                results[i - 1] = WithdrawResult({
                    token: address(lpInfo.underlyingTokens[i]),
                    amount: underlineTokenAmount
                });
            }
        } else {
            results = new WithdrawResult[](nTokens);
            for (uint16 i = 0; i < nTokens; ++i) {
                uint256 underlineTokenAmount = lpInfo.underlyingTokens[i].balanceOf(address(this)) - balancesBefore[i];
                results[i] = WithdrawResult({
                    token: address(lpInfo.underlyingTokens[i]),
                    amount: underlineTokenAmount
                });
            }
        }
    }

    function withdraw(address asset, uint256 amount) external returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        TransferHelper.safeTransferFrom(asset, msg.sender, address(this), amount);

        uint256 nTokensExpanded = lps[asset].underlyingTokensExpanded.length;
        WithdrawResult[] memory resultsExt = new WithdrawResult[](nTokensExpanded);
        uint256 resultsIndex = 0;
        WithdrawResult[] memory firstWithdrawResult = _withdrawInternal(asset, amount);
        uint256 nTokens = firstWithdrawResult.length;
        for (uint16 i = 0; i < nTokens; ++i) {
            if (isAdapterLP(firstWithdrawResult[i].token)) {
                WithdrawResult[] memory secondWithdrawResult = _withdrawInternal(
                    firstWithdrawResult[i].token,
                    firstWithdrawResult[i].amount
                );
                for (uint16 j = 0; j < nTokens; ++j) {
                    resultsExt[resultsIndex] = secondWithdrawResult[j];
                    ++resultsIndex;
                }
            } else {
                resultsExt[resultsIndex] = firstWithdrawResult[i];
                ++resultsIndex;
            }
        }

        results = new WithdrawResult[](resultsIndex);
        resultsIndex = 0;
        for (uint16 i = 0; i < nTokensExpanded; ++i) {
            address underlyingToken = resultsExt[i].token;
            if (underlyingToken != address(0)) {
                TransferHelper.safeTransfer(underlyingToken, msg.sender, resultsExt[i].amount);
                results[resultsIndex] = resultsExt[i];
                ++resultsIndex;
            }
        }
    }

    uint256[45] private __gap;
}

