pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "../BalancerAdapterWeighted.sol";
import "../../interfaces/oracle/ISuOracle.sol";

/**
 * @title BalancerAdapterWeightedUnsafe
 * @notice Needed to store getFiatPrice1e18Unsafe for tests
 */
abstract contract BalancerAdapterWeightedUnsafe {
    ISuOracle public _ORACLE;

    function __BalancerAdapterWeightedUnsafe__init(address _oracle) public {
        _ORACLE = ISuOracle(_oracle);
    }

    /// @notice Deprecated - Simple version of oracle that can be attacked with flash-loan, only for tests
    function _getFiatPrice1e18Unsafe(address asset, LPInfoAdapter memory lpInfo) internal view returns (uint256) {
        uint256 sumBalances = 0;
        uint256 nTokens = lpInfo.underlyingTokens.length;
        (,uint256[] memory newUnderlyingTokenBalances,) = lpInfo.vault.getPoolTokens(lpInfo.poolId);
        for (uint256 i = 0; i < nTokens; ++i) {
            address underlyingTokenAddress = address(lpInfo.underlyingTokens[i]);
            if (underlyingTokenAddress != asset) {
                sumBalances += newUnderlyingTokenBalances[i] * _ORACLE.getFiatPrice1e18(underlyingTokenAddress);
            }
        }

        return sumBalances / IERC20(asset).totalSupply();
    }
}

