pragma solidity ^0.8.0;

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";

import "../../access-control/SuAuthenticated.sol";
import "../../interfaces/adapters/ILPAdapter.sol";
import "../../vendor/quickswap/IUniswapV2Pair.sol";
import "../../vendor/quickswap/IUniswapV2Router01.sol";

struct QuickSwapLPInfo {
    address[2] underlyingTokens;
}

/**
 * @title QuickSwapPolygonAdapter
 * @notice Adapter for QuickSwap LP token in Polygon.
 * @dev See ILPAdapter interface for full details.
 */
contract QuickSwapPolygonAdapter is SuAuthenticated, ILPAdapter {
    mapping(address => QuickSwapLPInfo) private lps; // Mapping from LP token to pool
    ISuOracle public ORACLE;
    address public ROUTER;

    function initialize(address _authControl, address _oracle) public initializer {
        __suAuthenticatedInit(_authControl);
        ORACLE = ISuOracle(_oracle);
        ROUTER = 0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff;
    }

    /**
      * @notice Register the given LP token address and set the LP underlyingTokens
      * @param lp address of LP token
     **/
    function registerLP(address lp) external onlyAdmin {
        lps[lp].underlyingTokens[0] = IUniswapV2Pair(lp).token0();
        lps[lp].underlyingTokens[1] = IUniswapV2Pair(lp).token1();
    }

    function isAdapterLP(address asset) public view returns (bool) {
        return lps[asset].underlyingTokens[0] != address(0);
    }

    function getFiatPrice1e18(address asset) external view returns (uint256) {
        revert LPOracleNotReady();
        // Below it's unsafe oracle implementation
//        if (!isAdapterLP(asset)) revert IsNotLP(asset);
//        QuickSwapLPInfo memory lpInfo = lps[asset];
//
//        (uint112 _reserve0, uint112 _reserve1,) = IUniswapV2Pair(asset).getReserves();
//        uint256 sumBalances = _reserve0 * ORACLE.getFiatPrice1e18(lpInfo.underlyingTokens[0])
//            + _reserve1 * ORACLE.getFiatPrice1e18(lpInfo.underlyingTokens[1]);
//
//        return sumBalances / IUniswapV2Pair(asset).totalSupply();
    }

    function withdraw(address asset, uint256 amount) external returns (WithdrawResult[] memory results) {
        if (!isAdapterLP(asset)) revert IsNotLP(asset);
        TransferHelper.safeTransferFrom(asset, msg.sender, address(this), amount);
        QuickSwapLPInfo memory lpInfo = lps[asset];

        results = new WithdrawResult[](2);

        if (IUniswapV2Pair(asset).allowance(address(this), ROUTER) < amount) {
            IERC20(asset).approve(ROUTER, amount);
        }

        uint256[2] memory balancesBefore = [
            IERC20(lpInfo.underlyingTokens[0]).balanceOf(address(this)),
            IERC20(lpInfo.underlyingTokens[1]).balanceOf(address(this))
        ];

        IUniswapV2Router01(ROUTER).removeLiquidity(
            lpInfo.underlyingTokens[0],
            lpInfo.underlyingTokens[1],
            amount,
            0, // we can set amountAMin to 0 because it's used only in 1 line: require(amountA >= amountAMin, '...');
            0, // the same for amountBMin
            address(this), // In future we can decrease transfers and unwrap directly to user
            block.timestamp
        );

        for (uint i = 0; i < 2; ++i) {
            address underlyingToken = lpInfo.underlyingTokens[i];
            uint256 underlineTokenAmount = IERC20(underlyingToken).balanceOf(address(this)) - balancesBefore[i];
            results[i] = WithdrawResult({ token: underlyingToken, amount: underlineTokenAmount });
            TransferHelper.safeTransfer(underlyingToken, msg.sender, underlineTokenAmount);
        }
    }

    uint256[45] private __gap;
}

