    uint256 public priceUnsafeAfterFlashLoan;
    ISuOracle public ORACLE;

    constructor(address _oracle) {
        ORACLE = ISuOracle(_oracle);
    }

    function increaseObservationsCardinalityNext(address market, uint16 duration) external {
        IPMarket(market).increaseObservationsCardinalityNext(duration);
    }

    /**
        @param lpToken Address of asset to check price of
        @param lpAmount Amount of LP to withdraw
        @param adapter Address of LP Adapter
    */
    function checkLPOnFlashLoanAttack(
        address lpToken,
        uint256 lpAmount,
        address adapter
    ) external {
        IERC20(lpToken).approve(adapter, lpAmount);
        ILPAdapter(adapter).withdraw(lpToken, lpAmount);
        priceAfterFlashLoan = ORACLE.getFiatPrice1e18(lpToken);
        priceUnsafeAfterFlashLoan = IAdapterUnsafePrice(adapter).getFiatPrice1e18Unsafe(lpToken);
    }

    function getPriceAfterFlashLoan() public view returns (uint256) {
        return priceAfterFlashLoan;
    }

    function getUnsafePriceAfterFlashLoan() public view returns (uint256) {
        return priceUnsafeAfterFlashLoan;
    }
}

