// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "../interfaces/oracle/ISuChainlinkOracle.sol";
import "../access-control/SuAuthenticated.sol";

/**
 * @title SuChainlinkOracle
 * @notice Oracle that use Chainlink prices
 * @dev See ISuChainlinkOracle interface for full details.
 */
contract SuChainlinkOracle is ISuChainlinkOracle, SuAuthenticated {
    mapping (address => address) public assetToFeed;
    mapping (address => uint8) public assetToFeedDecimals;
    mapping (address => bool) public isPriceInETH; // We need to know is the feed price in USD or in ETH

    function initialize(address _authControl) public initializer {
        __suAuthenticatedInit(_authControl);
    }

    function setPriceBase(address asset, bool _isPriceInETH) external onlyDAO {
        isPriceInETH[asset] = _isPriceInETH;
    }

    function setAssetFeed(address asset, address chainlinkDataFeed, uint8 chainlinkDataFeedDecimals) external onlyDAO {
        assetToFeed[asset] = chainlinkDataFeed;
        // some decimals are different from real token decimals, so we need to store them
        assetToFeedDecimals[asset] = chainlinkDataFeedDecimals;
    }

    function getFiatPrice1e18(address asset) public view returns (uint256) {
        address feed = assetToFeed[asset];
        uint8 decimals = assetToFeedDecimals[asset];
        (
        /*uint80 roundID*/,
            int256 answer,
        /*uint256 startedAt*/,
        /*uint256 updatedAt*/,
        /*uint80 answeredInRound*/
        ) = AggregatorV3Interface(feed).latestRoundData();
        if (answer <= 0) revert BadPriceAnswer();
        // examples of answers and what we want to get:
        // ETH/USD = 134165000000 = 1341.65*1e8 =>(want) 1341*1e18
        // BTC/USD = 1928068190485 = 19,280.68190485*1e8 =>(want) 19k*1e18 * (18-8)[for asset decimals]
        // USDC/USD = 99990000 = 0.99990000*1e8 =>(want) => 0.9(9) * 1e18 * (18-6)[for asset decimals]
        int256 priceFeed1e18 = scalePrice(answer, decimals, 18);
        if (asset == address(0)) {
            return uint256(priceFeed1e18);
        } else {
            // TODO: add tests with different assets with different decimals in ETH
            uint8 assetDecimals = IERC20Metadata(asset).decimals();
            int256 scaledPrice1e18 = scalePrice(priceFeed1e18, assetDecimals, 18);
            if (isPriceInETH[asset]) {
                // address(0) mean ETH, that should be added in chainlink config
                return uint256(scaledPrice1e18) * getFiatPrice1e18(address(0)) / 1e18;
            } else {
                return uint256(scaledPrice1e18);
            }
        }

    }

    function scalePrice(int256 _price, uint8 _priceDecimals, uint8 _decimals) internal pure returns (int256) {
        if (_priceDecimals < _decimals) {
            return _price * int256(10 ** uint256(_decimals - _priceDecimals));
        } else if (_priceDecimals > _decimals) {
            return _price / int256(10 ** uint256(_priceDecimals - _decimals));
        }
        return _price;
    }
}

